// 각 스토리를 개별 아이템으로 분리
const items = $input.all();
return items.map(item => ({ json: item.json }));

// Claude API 요청 본문 준비
const story = $input.first().json;

const prompt = "당신은 시네마틱 뮤지컬 성경 영상 전문가입니다.

## 입력 스토리:
- 제목: " + story.title + "
- 구절 범위: " + story.book_name + " " + story.verses_range + "
- 핵심 주제: " + story.key_theme + "
- 등장인물: " + JSON.stringify(story.main_characters) + "
- 스토리 구조: " + story.story_arc + "
- 예상 길이: " + story.estimated_duration_sec + "초

## 과제:
이 스토리를 60-180초 뮤지컬 영상으로 제작하기 위한 프롬프트를 생성하세요.

## 3단계 시네마틱 구조:

### Act 1: 도입 (0-30초)
- 배경 설정, 인물 소개
- Runway 배경 영상
- 조용한 instrumental

### Act 2: 전개/갈등 (30-90초)
- 스토리 중심부
- Hedra 캐릭터 연기
- 음악 빌드업

### Act 3: 절정/결말 (90-180초)
- 뮤지컬 가창
- 립싱크 클로즈업
- 드라마틱 마무리

## 중요 원칙:
1. 해석 금지: 기록된 현상만 묘사
2. 중립성: 신학적 논쟁 배제
3. 1인칭 시점: 인물의 눈으로
4. 완결된 서사: 시작-전개-절정-결말

## 출력 형식 (JSON):
```json
{
  "narrative_structure": {
    "act1_setup": "도입부 요약 (30초)",
    "act2_conflict": "갈등/전개 (60초)",
    "act3_resolution": "절정/해결 (90초)"
  },
  "phase1_background": {
    "location": "장소 영문",
    "time_of_day": "시간대",
    "runway_prompt": "Runway용 완전한 영문 프롬프트 (30초 배경)"
  },
  "phase2_characters": [
    {
      "character": "캐릭터명",
      "timing": "10-40초",
      "description": "외형 영문 (나이, 복장 등)",
      "emotion": "감정 상태",
      "hedra_prompt": "Hedra용 완전한 영문 프롬프트"
    }
  ],
  "phase3_aria": {
    "vocal_lyrics": "뮤지컬 가사 (한국어, 스토리 전체 요약, 10-15줄)",
    "vocal_emotion_tags": "Fish Audio 감정 태그",
    "music_style": "음악 스타일",
    "suno_prompt": "Suno용 음악 프롬프트 (영문)"
  },
  "emotion_values": {
    "fear": 0.0,
    "resolve": 0.5,
    "confusion": 0.0
  }
}
```

지금 이 스토리를 위한 완전한 프롬프트를 생성하세요.";

return [{
  json: {
    ...story,
    claude_request_body: {
      model: "claude-3-haiku-20240307",
      max_tokens: 2000,
      messages: [
        {
          role: "user",
          content: prompt
        }
      ]
    }
  }
}];

// Claude 스토리 응답 파싱
// 원본 스토리 데이터는 이전 노드에서 가져오기
let original;
try {
  original = $('Claude 요청 본문 준비').first().json;
} catch (e) {
  // 폴백: 입력에서 직접 가져오기
  original = $input.first().json;
}
const claudeResponse = $input.first().json;

let narrative = {};
let phase1 = {};
let phase2 = [];
let phase3 = {};
let emotionValues = { fear: 0.0, resolve: 0.5, confusion: 0.0 };

try {
  const content = claudeResponse.content || [];
  const text = content.find(c => c.type === 'text')?.text || '';

  const jsonMatch = text.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);
    narrative = parsed.narrative_structure || {};
    phase1 = parsed.phase1_background || {};
    phase2 = parsed.phase2_characters || [];
    phase3 = parsed.phase3_aria || {};
    emotionValues = parsed.emotion_values || emotionValues;
  }
} catch (e) {
  console.error('JSON 파싱 실패:', e);
  // 폴백
  phase1 = {
    runway_prompt: `Wide cinematic shot of ${original.title}, biblical scene`
  };
  phase2 = [{
    character: original.main_characters?.[0] || 'biblical character',
    hedra_prompt: `Biblical scene with ${original.main_characters?.[0] || 'character'}`
  }];
  phase3 = {
    vocal_lyrics: `${original.title}\n${original.key_theme}`,
    suno_prompt: 'Cinematic orchestral epic dramatic'
  };
}

// Fish Audio 감정 태그
let fishTags = [];
if (emotionValues.fear > 0.7) fishTags.push('scared', 'nervous');
else if (emotionValues.fear > 0.4) fishTags.push('worried');
if (emotionValues.confusion > 0.6) fishTags.push('confused');
if (emotionValues.resolve > 0.8) fishTags.push('confident', 'determined');
if (fishTags.length === 0) fishTags.push('calm');

const phase3Tags = phase3.vocal_emotion_tags || '';
if (phase3Tags) fishTags.push(phase3Tags);
const fishEmotionString = `(${fishTags.join(')(')})` ;

// 주요 캐릭터 선택 (첫 번째)
const mainChar = phase2[0] || {};

return [{
  json: {
    story_id: original.id,
    book_name: original.book_name,
    verses_range: original.verses_range,
    title: original.title,
    verse_count: original.verse_count,
    estimated_duration_sec: original.estimated_duration_sec,

    // 서사 구조
    narrative_structure: JSON.stringify(narrative),

    // Phase 1
    phase1_location: phase1.location || '',
    phase1_runway_prompt: phase1.runway_prompt || '',
    phase1_duration: Math.ceil(original.estimated_duration_sec * 0.25),

    // Phase 2
    phase2_character_description: mainChar.description || '',
    phase2_hedra_prompt: mainChar.hedra_prompt || '',
    phase2_duration: Math.ceil(original.estimated_duration_sec * 0.35),

    // Phase 3
    phase3_vocal_lyrics: phase3.vocal_lyrics || '',
    phase3_suno_prompt: phase3.suno_prompt || '',
    phase3_duration: Math.ceil(original.estimated_duration_sec * 0.4),

    // 통합
    visual_prompt: phase1.runway_prompt || '',
    vocal_prompt: phase3.vocal_lyrics || '',
    music_prompt: phase3.suno_prompt || '',
    fish_emotion_tags: fishEmotionString,
    emotion_values: JSON.stringify(emotionValues)
  }
}];

// PostgreSQL UPDATE 쿼리 직접 생성 (n8n 파라미터 배열 버그 우회)
const data = $input.first().json;

// SQL 이스케이프 함수
function escapeSqlString(str) {
  if (str === null || str === undefined) return 'NULL';
  return "'" + String(str).replace(/'/g, "''").replace(/\\/g, "\\\\") + "'";
}

// 각 필드 값 준비
const narrative_structure = escapeSqlString(data.narrative_structure || null);
const phase1_location = escapeSqlString(data.phase1_location || null);
const phase1_runway_prompt = escapeSqlString(data.phase1_runway_prompt || null);
const phase2_character_description = escapeSqlString(data.phase2_character_description || null);
const phase2_hedra_prompt = escapeSqlString(data.phase2_hedra_prompt || null);
const phase3_vocal_lyrics = escapeSqlString(data.phase3_vocal_lyrics || null);
const phase3_suno_prompt = escapeSqlString(data.phase3_suno_prompt || null);
const visual_prompt = escapeSqlString(data.visual_prompt || null);
const vocal_prompt = escapeSqlString(data.vocal_prompt || null);
const music_prompt = escapeSqlString(data.music_prompt || null);
const fish_emotion_tags = escapeSqlString(data.fish_emotion_tags || null);
const emotion_values = escapeSqlString(data.emotion_values || null);
const story_id = data.story_id;

// SQL 쿼리 생성
const sqlQuery = `UPDATE story_units SET 
  narrative_structure = ${narrative_structure},
  phase1_location = ${phase1_location},
  phase1_runway_prompt = ${phase1_runway_prompt},
  phase2_character_description = ${phase2_character_description},
  phase2_hedra_prompt = ${phase2_hedra_prompt},
  phase3_vocal_lyrics = ${phase3_vocal_lyrics},
  phase3_suno_prompt = ${phase3_suno_prompt},
  visual_prompt = ${visual_prompt},
  vocal_prompt = ${vocal_prompt},
  music_prompt = ${music_prompt},
  fish_emotion_tags = ${fish_emotion_tags},
  emotion_values = ${emotion_values}
WHERE id = ${story_id}`;

return [{
  json: {
    ...data,
    sql_query: sqlQuery
  }
}];
